import styled from "styled-components";

export const FiltersWrapper = styled.div`
  border-bottom: 1px solid ${({ theme }) => theme.colors.greyMid};

  .activeFilters {
    box-sizing: border-box;
    padding: 10px 0;
  }
`;

export const FilterRowContainer = styled.div`
  width: 100%;
  display: flex;
  align-items: center;
  height: 40px;
  margin: 10px 0;

  h3 {
    font-size: 14px;
    width: 15px;
    font-weight: 700;
    color: ${({ theme }) => theme.colors.primaryBlue};
  }
`;

export const Combiner = styled.div`
  width: 80px;
  display: flex; 
  justify-content: center;

  h5 {
    color: white;
    width: 40px;
    border-radius: 2px;
    padding: 3px 0;
    text-align: center;
    background-color: ${({ theme }) => theme.colors.primaryBlue};
  }
`;
