import { useRef } from "react";
import styled from "styled-components";

type Props = {
  inputType: "number" | "text";
  onChange: (val: string) => void;
};
const InputWrapper = styled.div`
  margin-right: 20px;
  font-family: "Roboto";
  height: 35px;
  width: 190px;
  border: 1px solid ${({ theme }) => theme.colors.greyMid};
  cursor: pointer;
  position: relative;
  z-index: 10;
  border-radius: 3px;
  background-color: white;

  input {
    height: 35px;
    width: 190px;
    padding: 0 7px;
  }
`;

const InputBox = ({ inputType, onChange }: Props) => {
  const ref = useRef<HTMLInputElement>(null);

  ref.current &&
    ref.current.addEventListener("input", function () {
      if (inputType === "number")
        this.value = this.value.replace(/[^0-9 \,]/, "");
      onChange(this.value);
    });

    const handleChange = (e) => {
        e.target!.value = e.target.value.replace(/[^0-9 \,]/, "");
    }

  return (
    <InputWrapper>
      <input ref={ref} type="text" onInput={handleChange} />
    </InputWrapper>
  );
};

export default InputBox;
