import { useState } from "react";
import EmptyState from "./components/EmptyState/EmptyState";
import Table from "./components/Table/Table";
import { RulesetWrapper } from "./RulesetEngine.styles";
import { ThemeProvider } from "styled-components";
import { defaultTheme } from "./theme";
import "./fonts.css";
import { dummySchema } from "./utilities/dummyData";
import RulesList from "./components/RulesList/RulesList";
type Props = {};

const RulesetEngine = (props: Props) => {
  const [existingRules, setRules] = useState<RuleValues[]>([]);

  const switchView = () => {
    setRules([{ key: "placeholder", operator: "", value: "" }]);
  }

  const addRule = (newRule: RuleValues) => {
    setRules((prev) => [...prev, newRule]);
  };

  const removeRule = (key: string) => {
    const newValue = existingRules.filter(i => i.key !== key);
    setRules(newValue)
  };

  return (
    <ThemeProvider theme={defaultTheme}>
      <RulesetWrapper>
        <h4>Rules</h4>
        {!existingRules.length ? (
          <EmptyState switchView={switchView} />
        ) : (
          <>
            <RulesList
              schema={dummySchema}
              existingRules={existingRules.filter((i) => i.key !== "placeholder")}
              addRule={addRule}
              removeRule={removeRule}
            />
            <Table />
          </>
        )}
      </RulesetWrapper>
    </ThemeProvider>
  );
};

export default RulesetEngine;
