import { useState } from "react";
import styled from "styled-components";
import chevron from "../../assets/chevron.svg";
import StyledImage from "./StyledImage";

type Props = {
  options: {}[];
  onSelect: (val: string) => void;
  variant: DropdownType;
};

const DropdownWrapper = styled.div`
  font-family: "Roboto";
  height: 35px;
  width: 190px;
  border: 1px solid ${({ theme }) => theme.colors.greyDark};
`;
const InputValue = styled.div`
  height: 100%;
  width: 100%;
  display: flex;
  align-items: center;
  box-sizing: border-box;
  padding: 5px;
  border: 1px solid red;
  display: flex;
  justify-content: space-between;
`;

const CurrentValueText = styled.p`
  font-size: 14px;
`;

const Dropdown = ({ options, onSelect }: Props) => {
  const [currentValue, setCurrentValue] = useState<string>("Select");
  return (
    <DropdownWrapper>
      <InputValue>
        <CurrentValueText>{currentValue}</CurrentValueText>
        <StyledImage imgSrc={chevron} altText={"chevron"} customCSS={{ height: '30px', width: '30px', transform: 'rotate(90deg)'}} />
      </InputValue>
    </DropdownWrapper>
  );
};

export default Dropdown;
