import { useState } from "react";
import RuleRow from "../RuleRow/RuleRow";
import InputRow from "../InputRow/InputRow";
import { FiltersWrapper } from "./FiltersBar.styles";

type Props = {
  schema: RuleSchema[];
  existingRules: RuleValues[];
  addRule: (newRule: RuleValues) => void;
};

const FiltersBar = ({ schema, existingRules, addRule }: Props) => {
  return (
    <FiltersWrapper border={!!existingRules.length}>
      <InputRow schema={schema} addFilter={addRule} />
      <div className="existingRules">
        {
            existingRules.map((i:RuleValues, index: number) => 
                <RuleRow index={index} values={i} removeFilter={()=> console.log('hello')} />
            )
        }
      </div>
    </FiltersWrapper>
  );
};

export default FiltersBar;
