import styled from "styled-components";

export const FiltersWrapper = styled.div`
  border-bottom: 1px solid ${({ theme }) => theme.colors.greyMid};

  .activeFilters{
    box-sizing: border-box;
    padding: 10px 0;
  }
`;

export const FilterRowContainer = styled.div`
    width: 100%;
    display: flex;
    align-items: center;
    height: 40px;
`;
