import styled from "styled-components";

type Props = {
  inputType: "number" | "text";
  onChange: (val: string) => void;
};
const InputWrapper = styled.div`
  margin-right: 20px;
  font-family: "Roboto";
  height: 35px;
  width: 190px;
  border: 1px solid ${({ theme }) => theme.colors.greyMid};
  cursor: pointer;
  position: relative;
  z-index: 10;
  border-radius: 3px;
  background-color: white;

  input{
    height: 35px;
    width: 190px;
  }
`;

const InputBox = ({ inputType, onChange }: Props) => {
  return (
    <InputWrapper>
      <input type={inputType} />
    </InputWrapper>
  );
};

export default InputBox;
