import { useState } from "react";
import Button from "../../ui/Button"
import Dropdown from "../../ui/Dropdown"
import InputBox from "../../ui/InputBox";
import { InputRowWrapper } from "./InputRow.styles"

type Props = {}

const dummyKeys = [
  {
    text: "Number of nights",
  },
  {
    text: "Product ID",
  },
  {
    text: "Destination",
  },
  {
    text: "Tags",
  },
  {
    text: "Lead price",
  },
  {
    text: "Category",
  },
  {
    text: "Activities",
  },
];

const dummyOperators = [
  {
    text: "Is Equal to",
  },
  {
    text: "Is not equal to",
  },
  {
    text: "Is greater than",
  },
  {
    text: "Is not greater than",
  },
  {
    text: "Contains",
  },
  {
    text: "Does not contain",
  }
];

interface Values {
  key: string | null,
  operator: string | null,
  inputValue: string | string[] | null
}

const initialValues: Values = {
  key: null,
  operator: null,
  inputValue: null
}
const InputRow = (props: Props) => {
  const [inputValues, setValues] = useState<Values>(initialValues);

  const updateValue = (key: string, value: any) => {
    setValues({ ...inputValues, [key]: value });
  };

  const addFilter = () => {
    setValues(initialValues)
  };

  return (
    <InputRowWrapper>
      <Dropdown
        options={dummyKeys}
        variant={"rectangle"}
        onSelect={(val: string) => updateValue("key", val)}
      />
      <Dropdown
        options={dummyOperators}
        variant={"rounded"}
        onSelect={(val: string) => updateValue("operator", val)}
      />
      <InputBox
        val={inputValues.inputValue}
        inputType={"text"}
        onChange={(val: string) => updateValue("inputValue", val)}
      />
      <Button
        disabled={
          !!Object.values(inputValues).some((i) => i === null || i === "")
        }
        onClick={() =>addFilter()}
        text="+ Add rule"
      />
    </InputRowWrapper>
  );
}

export default InputRow
