import { useState } from "react";
import Button from "../../ui/Button"
import Dropdown from "../../ui/Dropdown"
import InputBox from "../../ui/InputBox";
import { dummyKeys, dummyOperators, dummySchema } from "../../utilities/dummyData";
import { InputRowWrapper } from "./InputRow.styles"

type Props = {
  schema: FilterSchema[]
}

interface Values {
  key: string | null,
  operator: string | null,
  value: string | null,
}

const initialValues: Values = {
  key: null,
  operator: null,
  value: null
}
const InputRow = (props: Props) => {
  const [inputValues, setValues] = useState<Values>(initialValues);
  const [ currentSchema, setSchema ] = useState<FilterSchema | null>(null)

  const updateValue = (key: string, value: any) => {
    setValues({ ...inputValues, [key]: value });
  };

  const addFilter = () => {
    setValues(initialValues)
  };

  return (
    <InputRowWrapper>
      <Dropdown
        value={inputValues.key}
        options={dummySchema.map(i => i.field)}
        variant={"rectangle"}
        onSelect={(val: string) => updateValue("key", val)}
      />
      <Dropdown
        disabled={!currentSchema}
        options={currentSchema?.operators || []}
        variant={"rounded"}
        onSelect={(val: string) => updateValue("operator", val)}
      />
      <InputBox
        value={inputValues.value || ''}
        inputType={"number"}
        onChange={(val: string) => updateValue("value", val)}
      />
      <Button
        disabled={
          !!Object.values(inputValues).some((i) => i === null || i === "")
        }
        onClick={() =>addFilter()}
        text="+ Add rule"
      />
    </InputRowWrapper>
  );
}

export default InputRow
