import { useState } from "react";
import styled from "styled-components";
import chevron from "../../assets/chevron.svg";
import StyledImage from "./StyledImage";

type Props = {
  options: {}[];
  onSelect: (val: string) => void;
  variant: DropdownType;
};

const DropdownWrapper = styled.div`
  font-family: "Roboto";
  height: 35px;
  width: 190px;
  border: 1px solid ${({ theme }) => theme.colors.greyDark};
  cursor: pointer;
`;
const InputValue = styled.div`
  height: 100%;
  width: 100%;
  display: flex;
  align-items: center;
  box-sizing: border-box;
  padding: 5px;
  display: flex;
  justify-content: space-between;
`;

const CurrentValueText = styled.p`
  font-size: 14px;
`;

const OptionsContainer = styled.div<{ open: boolean }>`
  width: 190px;
  transition: all 0.3s ease-in-out;
  border: 1px solid red;
  height: ${({ open }) => open ? '200px' : '0px'};
  opacity: ${({ open }) => open ? '1' : '0'};
`;

const Dropdown = ({ options, onSelect }: Props) => {
  const [currentValue, setCurrentValue] = useState<string>("Select");
  const [isOpen, toggleOpen] = useState<boolean>(false);
  return (
    <DropdownWrapper>
      <InputValue>
        <CurrentValueText>{currentValue}</CurrentValueText>
        <StyledImage imgSrc={chevron} altText={"chevron"} customCSS={{ height: '15px', width: '15px', transform: 'rotate(90deg)'}} />
      </InputValue>
      <OptionsContainer open={isOpen} />
    </DropdownWrapper>
  );
};

export default Dropdown;
