import { useState } from "react";
import FilterRow from "../FilterRow/FilterRow";
import InputRow from "../InputRow/InputRow";
import { FiltersWrapper } from "./FiltersBar.styles";

type Props = {
  schema: FilterSchema[];
};

const FiltersBar = ({ schema }: Props) => {
  const [activeFilters, setFilters] = useState<FilterValues[]>([
    { key: "Destination", operator: "not_equal_to", value: "Spain" },
  ]);

  const addFilter = (newFilter: FilterValues) => {
    setFilters(prev => [...prev, newFilter]);
  }

  return (
    <FiltersWrapper>
      <InputRow schema={schema} addFilter={addFilter} />
      <div className="activeFilters">
        {
            activeFilters.map((i:FilterValues, index: number) => 
                <FilterRow index={index} values={i} removeFilter={()=> console.log('hello')} />
            )
        }
      </div>
    </FiltersWrapper>
  );
};

export default FiltersBar;
